"exports.run = {
    usage: ['ewe'],
    hidden: ['eue'],
    use: 'mention or reply',
    category: 'user',
    async: async (m, {
        func,
        mecha,
        froms,
        calender
    }) => {
        if (m.quoted || m.text) {
            if (!(froms in global.db.users)) return m.reply('User data not found.')
            try {
                let user = global.db.users[m.sender];
                if (froms === m.sender) return m.reply(`Tidak bisa ngewe diri sendiri!`)
                if (froms === m.bot) return m.reply(`Tidak bisa ngewe bot!`)
                let target = global.db.users[froms];
                if (!target.gender) return m.reply(`@${froms.split('@')[0]} belum memiliki gender!`)
                if (user.gender == 'Perempuan') return m.reply('Lu cewe kocak, harusnya lu yang di ewe.')
                if (user.gender == 'Perempuan' && target.gender == 'Perempuan') return m.reply(`Kalian berdua *LESBI*`)
                if (user.gender == 'Laki-laki' && target.gender == 'Laki-laki') return m.reply(`Kalian berdua *GAY*`)
                if (!user.gender) return mecha.sendbut(m.chat, 'kamu belum memiliki gender', 'please select your gender below', [
                    ['Laki-laki', `${m.prefix}gender male`],
                    ['Perempuan', `${m.prefix}gender female`]
                ], m, {
                    expiration: m.expiration
                })
                if (typeof user.pasangan.id == 'undefined') return m.reply(`Kamu belum memiliki pasangan!\n- silahkan cari pasangan lalu ketik ${m.prefix}tembak (tag atau reply) pasangan kamu.`)
                if (froms !== user.pasangan.id) return m.reply(`@${froms.split('@')[0]} bukan pasangan kamu.`)
                let waktungewe = ['1 jam', '2 jam', '3 jam', '4 jam', '5 jam', '6 jam', '7 jam', '8 jam', '9 jam', '10 jam', '11 jam', '12 jam', '13 jam', '14 jam', '15 jam', '16 jam', '17 jam', '18 jam', '19 jam', '20 jam', '21 jam', '22 jam', '23 jam', '24 jam'].random()
                let amountngewe = 1000
                let desah = (Math.floor(Math.random() * amountngewe))
                let ewe = await mecha.sendMessage(m.chat, {
                    text: `${target.gender == 'Perempuan' ? '@' + froms.split('@')[0] + ' mendesah...\n🥵Ahhhh, Sakitttt!! >////<\n💦Crotttt.....' : '🥵 Mulai menggenjot.....'}`,
                    mentions: [froms]
                }, {
                    quoted: m,
                    ephemeralExpiration: m.expiration
                })
                await func.delay(3000)
                if (!user.desahan || isNaN(user.desahan)) user.desahan = 0;
                user.desahan += desah;
                let caption = `Kamu telah bersetubuh dengan @${froms.split('@')[0]} selama *${waktungewe}* dan mendapatkan *${desah}* 💦desahan..`;
                if (user.desahan >= 1000) caption += `\nSekarang @${froms.split('@')[0]} sedang hamil... Silahkan tunggu sampai melahirkan.`
                await mecha.sendMessage(m.chat, {
                    text: caption,
                    // edit: ewe.key,
                    mentions: [froms]
                }, {
                    quoted: ewe,
                    ephemeralExpiration: m.expiration
                })
                /*.then(_ => mecha.sendMessage(m.chat, {
                      delete: ewe.key
                  }))*/
                if (user.desahan >= 1000) {
                    await func.delay(1000 * 60 * 9)
                    let anak = random([1, 1, 1, 2, 1, 1, 1]);
                    user.anak += anak;
                    user.desahan -= 1000;
                    target.anak += anak;
                    target.desahan -= 1000;
                    user.listanak = user.listanak || [];
                    user.listanak.push({
                        ortu: froms,
                        anak: anak,
                        time: calender
                    })
                    target.listanak = target.listanak || [];
                    target.listanak.push({
                        ortu: m.sender,
                        anak: anak,
                        time: calender
                    })
                    await mecha.sendMessage(m.chat, {
                        text: `@${froms.split('@')[0]} telah melahirkan... ${anak == 1 ? 'Selamat kalian mendapatkan 1 anak' : 'Selamat kalian mendapatkan mendapatkan 2 bayi kembar'}.`,
                        mentions: [froms]
                    }, {
                        quoted: ewe,
                        ephemeralExpiration: m.expiration
                    })
                }
            } catch (error) {
                m.reply(error.message)
            }
        } else m.reply('Mention or Reply chat target.')
    },
    group: true,
    premium: true,
    location: 'plugins/user/ngewe.js'
}

function random(array) {
    array[Math.floor(Math.random() * array.length)];
}"
